DROP TABLE IF EXISTS `cms_complain`;
CREATE TABLE `cms_complain` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `url` varchar(200) DEFAULT NULL,
  `typename` varchar(30) DEFAULT NULL COMMENT '举报类型',
  `content` longtext COMMENT '举报内容',
  `picurl` varchar(50) DEFAULT NULL COMMENT '举报图片',
  `article_id` int(11) DEFAULT NULL COMMENT '被举报的文章',
  `user_id` int(11) DEFAULT NULL COMMENT '举报人',
  `created` datetime DEFAULT NULL COMMENT '举报时间',
  PRIMARY KEY (`id`)
